import React from 'react';
import axios from 'axios';
import {Card, Button,Row,Col,Container,Table,Form} from 'react-bootstrap';

class Admin extends React.Component {
       constructor(props){
            super(props);
            this.state = {
                usuarios: [],
                id: 0,
                name: '',
                password: '',
                type: 0
            };
        }
        componenDidMount(){
            axios.get("http://localhost:8080/api/usuario/")
            .then((res)=>{
                this.setState({
                    usuarios:res.data,
                    id:0,
                    name: '',
                    password: '',
                    type: 0
                })
            })
        }

        submit(event,id){
            event.preventDefault();
            if(id === 0){
                axios.post("http://localhost:8080/api/usuario/",{
                    name:this.state.name,
                    password:this.state.password,
                    type:this.state.type
                })
                .then((res)=>{
                    this.componenDidMount();
                })
            }
            else{
                axios.put("http://localhost:8080/api/usuario/PutUsuario/",{
                    id:this.state.id,
                    name:this.state.name,
                    password:this.state.password,
                    type:this.state.type
                })
                .then((res)=>{
                    this.componenDidMount();
                })
            }
        }



        render(){
            return (
                <>
    
                    <Container>
                        <Row style={{ paddingTop: '5em' }}>
    
                            <Col sm={4}>
                                <Card style={{ width: '18rem' }}>
                                    <Card.Header style={{ backgroundColor: '#5c5cff' }}>
                                        <Card.Title style={{ color: '#ffff' }}>Cadastro de Usuários</Card.Title>
                                    </Card.Header>
                                    <Card.Body>
    
                                        <Row>
                                            <Col md={8}>
                                                <Form>
                                                    <Form.Group controlId="exampleForm.ControlInput1">
                                                        <Form.Label>Nome Usuário</Form.Label>
                                                        <Form.Control type="text" />
                                                    </Form.Group>
                                                    <Form.Group controlId="exampleForm.ControlInput1">
                                                        <Form.Label>Senha</Form.Label>
                                                        <Form.Control type="text" />
                                                    </Form.Group>
                                                    <Form.Group controlId="exampleForm.ControlSelect1">
                                                        <Form.Label>Tipo Usuário</Form.Label>
                                                        <Form.Control as="select">
                                                            <option value="1">Administrador</option>
                                                            <option value="2">Triador</option>
                                                            <option value="3">Finalizador</option>
    
                                                        </Form.Control>
                                                    </Form.Group>
                                                    <Row>
                                                        <Col style={{ paddingTop: '20px' }}>
                                                            <Button variant="primary">Adicionar</Button>
                                                        </Col>
                                                    </Row>
                                                </Form>
                                            </Col>
                                        </Row>
    
    
    
                                    </Card.Body>
                                </Card>
                            </Col>
                            <Col sm={8}>
                                <Table striped bordered hover>
                                    <thead>
                                        <tr>
                                            <th>Nome</th>
                                            <th>Tipo de Usuário</th>
                                            <th>Editar</th>
                                            <th>Excluir</th>
                                        </tr>
                                    </thead>
                                    <tbody>
                                        <tr>
                                            <td>1</td>
                                            <td>Mark</td>
                                            <td><Button variant="outline-info">Editar</Button></td>
                                            <td><Button variant="outline-danger">Excluir</Button></td>
    
                                        </tr>
    
                                    </tbody>
                                </Table>
                            </Col>
    
                        </Row>
                    </Container>
                </>
    
    
    
            );
        }
       
    
}


export default Admin;
