import {React, useState} from 'react';
import {Button,Container,Form,Row} from 'react-bootstrap';


function initialState(){
    return {usuario:'', password:''};
}

const Login = () => {
    const [values, setValues] = useState(initialState);

    function onChange(event) {
        const {value ,name} = event.target;
        setValues({
            ...values,
            [name]:value,
        })
    }

    return(
            
            <Container>
               <Row style={{ paddingTop: '5em' }}>
                <Form>
                    <Form.Group controlId="formBasicEmail">
                        <Form.Label>Usu√°rio</Form.Label>
                        <Form.Control type="text" id="usuario" name="usuario" onChange={onChange} value={values.usuario}/>
                    </Form.Group>
                    <Form.Group controlId="formBasicPassword">
                        <Form.Label>Senha</Form.Label>
                        <Form.Control type="password" id="password" name="password" onChange={onChange}  value={values.password}/>
                    </Form.Group>
                    <Button variant="primary" type="submit">
                        Entrar
                    </Button>
                    </Form>
                </Row>
            </Container>

    );

}

export default Login;
