import  React from 'react';
import api from './Api';
import {Card, Button,Row,Col,Container,Table,Form} from 'react-bootstrap';

class Triador extends React.Component{
   
    constructor(props){
        super(props)
        this.state ={
            processos: [],
            id:0,
            description: '',
         
        }
        this.processoUsuario ={
            idprocesso_usuario:0,
            processo:0,
            usuario:0
          
        }
    }
    
     componenDidMount(){
            api.get("/processo/")
             .then((res)=>{
               //  console.log(res)
                 this.setState({
                     processos:res.data,
                     id:0,
                     description: '',
                 })
             })
         }

                
         submitProcesso(event){
            event.preventDefault();
          
                api.post("/processo/PostProcesso/",{
                    description:this.state.description,
               
                })
                .then(()=>{
                    this.componenDidMount();
                })
            
        }
        submitProcessoUsuario(event){
            event.preventDefault();
          
                api.post("/processoUsuario/PostProcessoUsuario/",{
                    processo:this.processoUsuario.processo,
                    usuario:this.processoUsuario.usuario
                })
                .then(()=>{
                    this.componenDidMount();
                })
            
        }


      
        render(){
            return (
                <>
    
                    <Container>
                        <Row style={{ paddingTop: '5em' }}>
    
                            <Col sm={5}>
                                <Card style={{ width: '18rem' }}>
                                    <Card.Header style={{ backgroundColor: '#5c5cff' }}>
                                        <Card.Title style={{ color: '#ffff' }}>Cadastro Processo</Card.Title>
                                    </Card.Header>
                                    <Card.Body>
    
                                        <Row>
                                            <Col md={8}>
                                                <Form onSubmit={(e)=>this.submitProcesso(e,this.state.id)}>
                                                    <Form.Group >
                                                        <Form.Label>Descrição</Form.Label>
                                                        <Form.Control onChange={(e)=>this.setState({description:e.target.value})} value={this.state.description} type="text" id="description" name="description"/>
                                                    </Form.Group>
                                                   
                                                    <Row>
                                                        <Col style={{ paddingTop: '20px' }}>
                                                            <Button variant="primary" type="submit" name="action">Adicionar</Button>
                                                        </Col>
                                                       
                                                    </Row>
                                                </Form>
                                            </Col>
                                        </Row>
    
                                    </Card.Body>
                                </Card>
                            </Col>
                            <Col sm={7}>
                         
                                <Table striped bordered hover>
                                    <thead>
                                        <tr>
                                            <th>#</th>
                                            <th>Processo</th>
                                          
                                        </tr>
                                    </thead>
                                    <tbody>
                                        {
                                            
                                            this.state.processos.map(processo=>
                                                
                                            <tr key={processo.id}>
                                                <td>{processo.id}</td>
                                                <td>{processo.description}</td>
                                                   
                                            </tr>
                                                
                                            )
                                            
                                        }
                                     
    
                                    </tbody>
                                </Table>
                            </Col>
    
                        </Row>
                    </Container>
                </>
    
    
    
            );
        }
      
    
}


export default Triador;
