import React from 'react';
import api from './Api';
import {Card, Button,Row,Col,Container,Table,Form} from 'react-bootstrap';

class Admin extends React.Component {
       constructor(props){
            super(props);
            this.state = {
                usuarios: [],
                id: 0,
                name: '',
                password: '',
                type: 0
            };
            
        }
        componenDidMount(){
            api.get("/usuario/")
            .then((res)=>{
                console.log(res)
                this.setState({
                    usuarios:res.data,
                    id:0,
                    name: '',
                    password: '',
                    type: 0
                })
            })
        }
        
        submit(event,id){
            event.preventDefault();
            if(id === 0){
                api.post("/usuario/PostUsuario/",{
                    name:this.state.name,
                    password:this.state.password,
                    type:this.state.type
                })
                .then(()=>{
                    this.componenDidMount();
                })
            }
            else{
                api.put("/api/usuario/PutUsuario/",{
                    id:this.state.id,
                    name:this.state.name,
                    password:this.state.password,
                    type:this.state.type
                })
                .then(()=>{
                    this.componenDidMount();
                })
            }
        }

        delete(id){
            api.delete(`/api/usuario/${id}`)
            .then(()=>{
                this.componenDidMount();
            })
        }
        edit(id){
            api.get(`/api/usuario/GetById/${id}`)
            .then((res)=>{
                console.log(res);
                this.setState({
                    id:res.data.id,
                    name:res.data.name,
                    password:res.data.password,
                    type:res.data.type
                })
            })
        }

        render(){
            
            return (
                <>
    
                    <Container>
                        <Row style={{ paddingTop: '5em' }}>
    
                            <Col sm={4}>
                                <Card style={{ width: '18rem' }}>
                                    <Card.Header style={{ backgroundColor: '#5c5cff' }}>
                                        <Card.Title style={{ color: '#ffff' }}>Cadastro de Usuários</Card.Title>
                                    </Card.Header>
                                    <Card.Body>
    
                                        <Row>
                                            <Col md={8}>
                                                <Form onSubmit={(e)=>this.submit(e,this.state.id)}>
                                                    <Form.Group controlId="exampleForm.ControlInput1">
                                                        <Form.Label>Nome Usuário</Form.Label>
                                                        <Form.Control onChange={(e)=>this.setState({name:e.target.value})} value={this.state.name} type="text" />
                                                    </Form.Group>
                                                    <Form.Group controlId="exampleForm.ControlInput1">
                                                        <Form.Label>Senha</Form.Label>
                                                        <Form.Control onChange={(e)=>this.setState({password:e.target.value})} value={this.state.password} type="text" />
                                                    </Form.Group>
                                                    <Form.Group controlId="exampleForm.ControlSelect1">
                                                        <Form.Label>Tipo Usuário</Form.Label>
                                                        <Form.Control as="select" onChange={(e)=>this.setState({type:e.target.value})} value={this.state.type} >
                                                            <option value="1">Administrador</option>
                                                            <option value="2">Triador</option>
                                                            <option value="3">Finalizador</option>
    
                                                        </Form.Control>
                                                    </Form.Group>
                                                    <Row>
                                                        <Col style={{ paddingTop: '20px' }}>
                                                            <Button variant="primary" type="submit" name="action">Adicionar</Button>
                                                        </Col>
                                                    </Row>
                                                </Form>
                                            </Col>
                                        </Row>
    
    
    
                                    </Card.Body>
                                </Card>
                            </Col>
                            <Col sm={8}>
                                <Table striped bordered hover>
                                    <thead>
                                        <tr>
                                            <th>#</th>
                                            <th>Nome</th>
                                            <th>Tipo de Usuário</th>
                                            <th>Editar</th>
                                            <th>Excluir</th>
                                        </tr>
                                    </thead>
                                    <tbody>
                                        {
                                            this.state.usuarios.map(usuario=>
                                                
                                            <tr>
                                                <td>{usuario.id}</td>
                                                <td>{usuario.name}</td>
                                                <td>{usuario.type}</td>
                                                <td><Button onClick={(e)=>this.edit(usuario.id)} variant="outline-info" type="submit" name="action">Editar</Button></td>
                                                <td><Button onClick={(e)=>this.delete(usuario.id)} variant="outline-danger" type="button" name="action">Excluir</Button></td>
        
                                            </tr>
                                                
                                            )
                                        }
                                     
    
                                    </tbody>
                                </Table>
                            </Col>
    
                        </Row>
                    </Container>
                </>
    
    
    
            );
        }
       
    
}


export default Admin;
