import { React,useState }from 'react';
import api from './Api';
import {Card, Button,Row,Col,Container,Table,Form} from 'react-bootstrap';

function Admin () {
   
    const [state, setState] = useState({usuarios:[],id:0,name:'',password:'',type: 0});
  
   function onChange(event){
        const {name,value} = event.target;
        setState({...state,[name]:value})
    }

    function componenDidMount(){
            api.get("/usuario/")
             .then((res)=>{
               //  console.log(res)
                 this.setState({
                     usuarios:res.data,
                     id:0,
                     name: '',
                     password: '',
                     type: 0
                 })
             })
         }

                
         function submit(event,id){
            event.preventDefault();
            if(id === 0){
                api.post("/usuario/PostUsuario/",{
                    name:this.state.name,
                    password:this.state.password,
                    type:this.state.type
                })
                .then(()=>{
                    this.componenDidMount();
                })
            }
            else{
                api.put("/usuario/PutUsuario/",{
                    id:this.state.id,
                    name:this.state.name,
                    password:this.state.password,
                    type:this.state.type
                })
                .then(()=>{
                    this.componenDidMount();
                })
            }
        }

        function excluir(id){
            api.delete(`/usuario/${id}`)
            .then(()=>{
                this.componenDidMount();
            })
        }

        function editar(id){
            api.get(`/usuario/GetById/${id}`)
            .then((res)=>{
              //  console.log(res);
                this.setState({
                    id:res.data.id,
                    name:res.data.name,
                    password:res.data.password,
                    type:res.data.type
                })
            })
        }

            return (
                <>
    
                    <Container>
                        <Row style={{ paddingTop: '5em' }}>
    
                            <Col sm={4}>
                                <Card style={{ width: '18rem' }}>
                                    <Card.Header style={{ backgroundColor: '#5c5cff' }}>
                                        <Card.Title style={{ color: '#ffff' }}>Cadastro de Usuários</Card.Title>
                                    </Card.Header>
                                    <Card.Body>
    
                                        <Row>
                                            <Col md={8}>
                                                <Form onSubmit={(e)=>submit(e,state.id)}>
                                                    <Form.Group >
                                                        <Form.Label>Nome Usuário</Form.Label>
                                                        <Form.Control onChange={onChange} value={state.name} type="text" id="name" name="name"/>
                                                    </Form.Group>
                                                    <Form.Group >
                                                        <Form.Label>Senha</Form.Label>
                                                        <Form.Control onChange={onChange} value={state.password} type="text" id="password" name="password"/>
                                                    </Form.Group>
                                                    <Form.Group >
                                                        <Form.Label>Tipo Usuário</Form.Label>
                                                        <Form.Control as="select" onChange={onChange} value={state.type} id="type" name="type">
                                                            <option value="0">Selecione</option>
                                                            <option value="1">Administrador</option>
                                                            <option value="2">Triador</option>
                                                            <option value="3">Finalizador</option>
    
                                                        </Form.Control>
                                                    </Form.Group>
                                                    <Row>
                                                        <Col style={{ paddingTop: '20px' }}>
                                                            <Button variant="primary" type="submit" name="action">Adicionar</Button>
                                                        </Col>
                                                    </Row>
                                                </Form>
                                            </Col>
                                        </Row>
    
                                    </Card.Body>
                                </Card>
                            </Col>
                            <Col sm={8}>
                               <Button variant="info" type="button" onClick={componenDidMount()}>Adicionar</Button>
                                <Table striped bordered hover>
                                    <thead>
                                        <tr>
                                            <th>#</th>
                                            <th>Nome</th>
                                            <th>Tipo de Usuário</th>
                                            <th>Editar</th>
                                            <th>Excluir</th>
                                        </tr>
                                    </thead>
                                    <tbody>
                                        {
                                            
                                            this.state.usuarios.map(usuario=>
                                                
                                            <tr key={usuario.id}>
                                                <td>{usuario.id}</td>
                                                <td>{usuario.name}</td>
                                                <td>{usuario.type}</td>
                                                <td><Button onClick={(e)=>editar(usuario.id)} variant="outline-info" type="submit" name="action">Editar</Button></td>
                                                <td><Button onClick={(e)=>excluir(usuario.id)} variant="outline-danger" type="button" name="action">Excluir</Button></td>
        
                                            </tr>
                                                
                                            )
                                            
                                        }
                                     
    
                                    </tbody>
                                </Table>
                            </Col>
    
                        </Row>
                    </Container>
                </>
    
    
    
            );
    
      
    
}


export default Admin;
