import React,{useState} from 'react';
import api from './Api';
import {Card, Button,Row,Col,Container,Table,Form} from 'react-bootstrap';

class Finalizador extends React.Component {
        
    constructor(props){
        super(props)
        this.state ={
            processos: [],
            id:0,
            description: '',
            processo:0,
            usuario:""
         
        }
    }

    componenDidMount(){
        api.post("/processoUsuario/GetProcessoPendente")
         .then((res)=>{
           //  console.log(res)
             this.setState({
                 processos:res.data,
                
             })
         })
     }
       
    submitProcessoUsuario(event){
        event.preventDefault();
        api.put("/processoUsuario/PutProcessoUsuario/",{
            usuario:this.props.location.name,
            processo:this.state.processo
        })
        .then(()=>{
            this.componenDidMount();
            this.resetUsuario();
        })
    
    }

    render(){
        return (
                <>
                <Container>

                    <Row style={{ paddingTop: '2em' }}>

                            <Col md={12}>
                                <Card>
                                    <Card.Header style={{ backgroundColor: '#5c5cff' }}>
                                        <Card.Title style={{ color: '#ffff' }}>Lista de Processos</Card.Title>
                                    </Card.Header>
                                    <Card.Body>
                                    <Row>
                                                <Col style={{ paddingTop: '20px' }}>
                                                    <Button variant="primary" onClick={(e)=>this.componenDidMount()} type="button">Atualizar Tabela</Button>
                                                </Col>
                                            </Row>
                                        <Row>
                                            <Col md={12}>
                                            <Table striped bordered hover>
                                                <thead style={{ backgroundColor: '#457b9d',color:'white'}}>
                                                    <tr>
                                                        <th>#</th>
                                                        <th>Processo</th>
                                                        <th>Atribuir</th>
                                                    </tr>
                                                </thead>
                                                <tbody>
                                                        <td>#</td>
                                                        <td>Processo</td>
                                                        <td><Form >
                                                                <Form.Group >
                                                                    <Form.Label>Adicione o parecer </Form.Label>
                                                                    <Form.Control as="textarea" rows={3} id="parecer" name="parecer"/>
                                                                    <Form.Control type="text" id="processo" name="processo" hidden/>
                                                                    <Form.Control type="text" id="usuario" name="usuario" hidden/>
                                                                </Form.Group>
                                                            
                                                                <Row>
                                                                    <Col style={{ paddingTop: '20px' }}>
                                                                        <Button variant="primary" type="submit" name="action">Salvar</Button>
                                                                    </Col>
                                                                
                                                                </Row>
                                                            </Form>
                                                        </td>
                
                                                </tbody>
                                            </Table>
                                            </Col>
                                        </Row>

                                    </Card.Body>
                                </Card>
                            </Col>
                        </Row>
                </Container>
                </>
        );
    }
} 


export default Finalizador;
