import  React from 'react';
import api from './Api';
import {Card, Button,Row,Col,Container,Table,Form} from 'react-bootstrap';

class Triador extends React.Component{
   
    constructor(props){
        super(props)
        this.state ={
            processos: [],
            id:0,
            description: '',
            processo:0,
            name:""
         
        }
     
    }
    
     componenDidMount(){
            api.get("/processo/")
             .then((res)=>{
               //  console.log(res)
                 this.setState({
                     processos:res.data,
                    
                 })
             })
         }

                
         submitProcesso(event){
            event.preventDefault();
          
                api.post("/processo/PostProcesso/",{
                    description:this.state.description,
               
                })
                .then((res)=>{
                    this.componenDidMount();
                })
            
        }
        submitProcessoUsuario(event){
            event.preventDefault();
            api.post("/processo/PostProcesso/",{
                description:this.state.description,
           
            })
            .then(()=>{
                this.componenDidMount();
            })
        
        }

        reset(){
            this.setState({
                description: '',
                
            })
        }
           
        render(){
            return (
                <>
    
                    <Container>
                        <Row style={{ paddingTop: '2em' }}>
    
                            <Col sm={12}>
                                <Card>
                                    <Card.Header style={{ backgroundColor: '#5c5cff' }}>
                                        <Card.Title style={{ color: '#ffff' }}>Cadastrar Processo e Atribuição</Card.Title>
                                    </Card.Header>
                                    <Card.Body>
    
                                        <Row>
                                            <Col md={10}>
                                                <Form onSubmit={(e)=>this.submitProcesso(e,this.state.id)}>
                                                    <Form.Group >
                                                        <Form.Label>Descrição</Form.Label>
                                                        <Form.Control onChange={(e)=>this.setState({description:e.target.value})} value={this.state.description} type="text" id="description" name="description"/>
                                                    </Form.Group>
                                                   
                                                    <Row>
                                                        <Col style={{ paddingTop: '20px' }}>
                                                            <Button variant="primary" type="submit" name="action">Adicionar</Button>
                                                        </Col>
                                                       
                                                    </Row>
                                                </Form>
                                            </Col>
                                        </Row>
    
                                        <Row >
                                            <Col md={12}>
                                                <Form onSubmit={(e)=>this.submitProcessoUsuario(e,this.state.id)}>
                                                    <Form.Group >
                                                      <Form.Label>Nome Usuário</Form.Label>
                                                           <Form.Control onChange={(e)=>this.setState({name:e.target.value})} value={this.state.name} type="text" id="name" name="name"/>
                                                   </Form.Group>
                                                   
                                                    <Row>
                                                        <Col style={{ paddingTop: '20px' }}>
                                                            <Button variant="primary" type="submit" name="action">Salvar</Button>
                                                        </Col>
                                                       
                                                    </Row>
                                                </Form>
                                            </Col>
                                        </Row>
                                    </Card.Body>
                                </Card>
                            </Col>
                            <Col sm={7}>
                         
                                <Table striped bordered hover>
                                    <thead style={{ backgroundColor: '#5c5cff',color:'white'}}>
                                        <tr>
                                            <th>#</th>
                                            <th>Processo</th>
                                            <th>Atribuir</th>
                                        </tr>
                                    </thead>
                                    <tbody>
                                        {
                                            
                                            this.state.processos.map(processo=>
                                                
                                            <tr key={processo.id}>
                                                <td>{processo.id}</td>
                                                <td>{processo.description}</td>
                                                <td><Button onClick={(e)=>this.editar(processo.id)} variant="outline-info" type="submit" name="action">Editar</Button></td>
                                            </tr>
                                                
                                            )
                                            
                                        }
                                     
    
                                    </tbody>
                                </Table>
                            </Col>
    
                        </Row>
                    </Container>
                </>
    
    
    
            );
        }
      
    
}


export default Triador;
